<?php get_header(); ?><!--	what-in-box__section -->	<section class="what-in-box__section">		<div class="container wrapper">			<?php if ( have_posts() ) :  ?>			 <div class="section_title">					<h2>Поиск по запросу: "<?php echo get_search_query(); ?>"</h2>			 </div>			 			 <div class="row justify-content-center">			 	<div class="col-xl-10">			 						 <div class="what-in-box__holder mt-5">	 						<?php 					while ( have_posts() ) {						the_post();						/**						 * Hook: woocommerce_shop_loop.						 *						 * @hooked WC_Structured_Data::generate_product_data() - 10						 */						do_action( 'woocommerce_shop_loop' );						wc_get_template_part( 'content', 'product' );					}					?>				 </div>			 			 	</div>			 </div>			 <?php else: ?>			 <div class="section_title">					<h2>Ничего не найдено.</h2>			 </div>			<?php endif; ?>				</div>	</section><!--	what-in-box__section end -->  <?php get_footer(); ?>